import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const [selectedBrands, setSelectedBrands] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler çekilirken hata oluştu:", error);
      }
    }
    fetchProducts();
  }, []);

  const uniqueBrands = [...new Set(products.map(p => p.brandName))];

  const handleBrandChange = (brand) => {
    setSelectedBrands(prev =>
      prev.includes(brand)
        ? prev.filter(b => b !== brand)
        : [...prev, brand]
    );
  };

  const filtered = products.filter(product => {
    const matchesSearch =
      (product.brandName + " " + product.modelName + " " + product.productName)
        .toLowerCase()
        .includes((searchTerm || "").toLowerCase());

    const matchesBrand = selectedBrands.length === 0 || selectedBrands.includes(product.brandName);

    return matchesSearch && matchesBrand;
  });

  return (
    <div className="container-fluid mt-4">
      <div className="row">
        {/* Sol Filtreleme Paneli */}
        <div className="col-md-3 mb-4">
          <div className="p-3 border rounded shadow-sm sticky-top" style={{ top: '80px' }}>
            <h5 className="mb-3">Filtrele</h5>
            <strong className="d-block mb-2">Marka</strong>
            {uniqueBrands.map(brand => (
              <div key={brand} className="form-check mb-2">
                <input
                  className="form-check-input"
                  type="checkbox"
                  value={brand}
                  id={`brand-${brand}`}
                  checked={selectedBrands.includes(brand)}
                  onChange={() => handleBrandChange(brand)}
                />
                <label className="form-check-label" htmlFor={`brand-${brand}`}>
                  {brand}
                </label>
              </div>
            ))}
          </div>
        </div>

        {/* Ürün Kartları */}
        <div className="col-md-9">
          <h2 className="text-center mb-4">Ürünler</h2>
          <div className="row">
            {filtered.map(product => (
              <div
                key={product.productID}
                className="col-12 col-sm-6 col-lg-4 col-xl-3 mb-4"
                onClick={() => navigate(`/product/${product.productID}`)}
                style={{ cursor: "pointer" }}
              >
                <div
                  className="card h-100 border-0 shadow-sm"
                  style={{ transition: "transform 0.2s ease-in-out" }}
                  onMouseEnter={e => (e.currentTarget.style.transform = "scale(1.02)")}
                  onMouseLeave={e => (e.currentTarget.style.transform = "scale(1)")}
                >
                  <div className="card-body text-center">
                    <img
                      src={product.ecommercePageLogo}
                      alt={product.ecommercePageName}
                      style={{ width: 60 }}
                      className="mb-2"
                    />
                    <h5 className="card-title">{product.brandName} {product.modelName}</h5>
                    <p className="text-muted small mb-1">{product.productName}</p>
                    <p className="text-secondary small">
                      {product.ramCapacity} / {product.storage} / {product.batteryCapacity}
                    </p>
                  </div>
                  <div className="card-footer bg-white text-center border-0">
                    <h6 className="fw-bold text-success">
                      {product.discountedPrice
                        ? `${product.discountedPrice} TL`
                        : `${product.normalPrice} TL`}
                    </h6>
                    <span className="badge bg-light text-dark mt-2" style={{ fontSize: "0.85rem" }}>
                      {product.ecommercePageName}
                    </span>
                  </div>
                </div>
              </div>
            ))}
            {filtered.length === 0 && (
              <div className="col-12 text-center">
                <p className="text-muted mt-4">Aradığınız kriterlere uygun ürün bulunamadı.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductList;
