import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom"; // EKLENDİ

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate(); // EKLENDİ

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler yüklenirken hata oluştu.");
      }
    }
    fetchProducts();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Ürün Listesi</h1>
      <div className="row">
        {products.map((product) => (
          <div
            key={product.productDetailID}
            className="col-md-4 mb-4"
            onClick={() => handleProductClick(product.productDetailID)}
            style={{ cursor: "pointer" }}
          >
            <div className="card h-100 shadow-sm">
              <div className="card-body">
                <h5 className="card-title">{product.brandName} {product.modelName}</h5>
                <p className="card-text">
                  RAM: {product.ram} <br />
                  Depolama: {product.storage} <br />
                  Batarya: {product.battery} <br />
                  Renk: {product.color} <br />
                </p>
              </div>
              <div className="card-footer">
                <h5 className="text-primary">{product.price} TL</h5>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
