import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPlatformScores } from "../api/productApi"; // ðŸ”¥ Yeni API fonksiyon
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

const ProductDetail = () => {
  const { id } = useParams();
  const [platformData, setPlatformData] = useState([]);

  useEffect(() => {
    async function fetchPlatformScores() {
      try {
        const data = await getPlatformScores(id);
        setPlatformData(data);
      } catch (error) {
        console.error("Platform skorlarÄ± yÃ¼klenirken hata oluÅŸtu.");
      }
    }
    fetchPlatformScores();
  }, [id]);

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-5">ÃœrÃ¼n DetayÄ± - ID: {id}</h1>

      <div className="row">
        {platformData.length > 0 ? (
          platformData.map((platform, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card h-100 shadow-sm">
                <div className="card-body text-center">
                  <h5 className="card-title">{platform.platform}</h5>
                  <h6 className="text-success">Memnuniyet: {platform.memnuniyet}%</h6>

                  <ResponsiveContainer width="100%" height={150}>
                    <BarChart
                      layout="vertical"
                      data={[
                        { name: "Kalite", puan: platform.kalite },
                        { name: "Fiyat/Performans", puan: platform.fiyatPerformans },
                        { name: "Kargolama", puan: platform.kargolama },
                      ]}
                      margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                    >
                      <XAxis type="number" domain={[0, 100]} hide />
                      <YAxis type="category" dataKey="name" />
                      <Tooltip />
                      <Bar dataKey="puan" fill="#007bff" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">Platform verisi bulunamadÄ±.</p>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
