import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  
  const defaultFilters = {
    brands: [],
    ram: [],
    storage: [],
    color: [],
    price: { min: "", max: "" }
  };
  
  const [pendingFilters, setPendingFilters] = useState(defaultFilters);
const [activeFilters, setActiveFilters] = useState(defaultFilters);

  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler çekilirken hata oluştu:", error);
      }
    }
    fetchProducts();
  }, []);

  const uniqueBrands = [...new Set(products.map((p) => p.brandName))];
  const uniqueRAMs = [...new Set(products.map((p) => p.ramCapacity))];
  const uniqueStorages = [...new Set(products.map((p) => p.storage))];
  const uniqueColors = [...new Set(products.map((p) => p.color))];

  const handleCheckboxChange = (field, value) => {
    setPendingFilters((prev) => {
      const arr = prev[field];
      return {
        ...prev,
        [field]: arr.includes(value)
          ? arr.filter((item) => item !== value)
          : [...arr, value],
      };
    });
  };

  const filtered = products.filter((product) => {
    const matchesSearch =
      (product.brandName + " " + product.modelName + " " + product.productName)
        .toLowerCase()
        .includes((searchTerm || "").toLowerCase());

    const matchesBrand =
      activeFilters.brands.length === 0 ||
      activeFilters.brands.includes(product.brandName);

    const matchesRAM =
      activeFilters.ram.length === 0 ||
      activeFilters.ram.includes(product.ramCapacity);

    const matchesStorage =
      activeFilters.storage.length === 0 ||
      activeFilters.storage.includes(product.storage);

    const matchesColor =
      activeFilters.color.length === 0 ||
      activeFilters.color.includes(product.color);

    const price = product.discountedPrice || product.normalPrice || 0;
    const minOk =
      activeFilters.price.min === "" ||
      price >= parseFloat(activeFilters.price.min);
    const maxOk =
      activeFilters.price.max === "" ||
      price <= parseFloat(activeFilters.price.max);

    return (
      matchesSearch &&
      matchesBrand &&
      matchesRAM &&
      matchesStorage &&
      matchesColor &&
      minOk &&
      maxOk
    );
  });

  return (
    <div className="container-fluid mt-4">
      <div className="row">
        {/* Sol Filtre Paneli */}
        <div className="col-md-3 mb-4">
          <div className="p-4 rounded shadow-sm bg-white border" style={{ position: "sticky", top: "80px" }}>
            <h5 className="mb-4 border-bottom pb-2 text-primary">Filtrele</h5>

            {/* Marka */}
            <strong className="d-block mb-2">Marka</strong>
            {uniqueBrands.map((brand) => (
              <div key={brand} className="form-check mb-1">
                <input
                  className="form-check-input"
                  type="checkbox"
                  id={`brand-${brand}`}
                  checked={pendingFilters.brands.includes(brand)}
                  onChange={() => handleCheckboxChange("brands", brand)}
                />
                <label className="form-check-label" htmlFor={`brand-${brand}`}>{brand}</label>
              </div>
            ))}
            

            {/* RAM */}
            <strong className="d-block mt-3">RAM</strong>
            {uniqueRAMs.map((ram) => (
              <div key={ram} className="form-check mb-1">
                <input
                  className="form-check-input"
                  type="checkbox"
                  id={`ram-${ram}`}
                  checked={pendingFilters.ram.includes(ram)}
                  onChange={() => handleCheckboxChange("ram", ram)}
                />
                <label className="form-check-label" htmlFor={`ram-${ram}`}>{ram}</label>
              </div>
            ))}

            {/* Depolama */}
            <strong className="d-block mt-3">Depolama</strong>
            {uniqueStorages.map((storage) => (
              <div key={storage} className="form-check mb-1">
                <input
                  className="form-check-input"
                  type="checkbox"
                  id={`storage-${storage}`}
                  checked={pendingFilters.storage.includes(storage)}
                  onChange={() => handleCheckboxChange("storage", storage)}
                />
                <label className="form-check-label" htmlFor={`storage-${storage}`}>{storage}</label>
              </div>
            ))}

            {/* Renk */}
            <strong className="d-block mt-3">Renk</strong>
            {uniqueColors.map((color) => (
              <div key={color} className="form-check mb-1">
                <input
                  className="form-check-input"
                  type="checkbox"
                  id={`color-${color}`}
                  checked={pendingFilters.color.includes(color)}
                  onChange={() => handleCheckboxChange("color", color)}
                />
                <label className="form-check-label" htmlFor={`color-${color}`}>{color}</label>
              </div>
            ))}

            {/* Fiyat Aralığı */}
            <strong className="d-block mt-3">Fiyat</strong>
            <div className="d-flex gap-2 mb-3">
              <input
                type="number"
                className="form-control form-control-sm"
                placeholder="Min"
                value={pendingFilters.price.min}
                onChange={(e) =>
                  setPendingFilters({
                    ...pendingFilters,
                    price: { ...pendingFilters.price, min: e.target.value },
                  })
                }
              />
              <input
                type="number"
                className="form-control form-control-sm"
                placeholder="Max"
                value={pendingFilters.price.max}
                onChange={(e) =>
                  setPendingFilters({
                    ...pendingFilters,
                    price: { ...pendingFilters.price, max: e.target.value },
                  })
                }
              />
            </div>

            <div className="d-grid gap-2 mt-3">
  <button
    className="btn btn-success"
    onClick={() => setActiveFilters({ ...pendingFilters })}
  >
    Filtrele
  </button>

  <button
    className="btn btn-outline-secondary"
    onClick={() => {
      setPendingFilters(defaultFilters);
      setActiveFilters(defaultFilters);
    }}
  >
    Temizle
  </button>
</div>

          </div>
        </div>

        <div className="col-md-9">
  <h2 className="text-center mb-4">Ürünler</h2>
  <div className="row" >
    {filtered.length > 0 ? (
      filtered.map((product) => (
        <div
          key={product.productID}
          className="col-12 col-sm-6 col-lg-4 col-xl-3 mb-4"
          onClick={() => navigate(`/product/${product.productID}`)}
          style={{backgroundColor: "#e7f0fe", padding: "20px", borderRadius: "10px"}}
        >
          <div
            className="card border-0 shadow-sm h-100"
            style={{
              borderRadius: "15px",
              overflow: "hidden",
              transition: "transform 0.3s, box-shadow 0.3s"
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = "scale(1.02)";
              e.currentTarget.style.boxShadow = "0 8px 20px rgba(0,0,0,0.1)";
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = "scale(1)";
              e.currentTarget.style.boxShadow = "0 4px 12px rgba(0,0,0,0.06)";
            }}
          >
            {/* Üst Görsel Alanı */}
            <div className="p-3 text-center bg-light">
              <img
                src={product.ecommercePageLogo || "/logos/default.png"}
                alt={product.ecommercePageName}
                style={{ width: 60, marginBottom: 10 }}
              />
              <img
                src="/img/placeholder-phone.png"
                alt="Ürün görseli"
                className="img-fluid"
                style={{ maxHeight: 120, objectFit: "contain" }}
              />
            </div>

            {/* İçerik */}
            <div className="card-body text-center">
              <h6 className="fw-bold mb-1 text-dark">
                {product.brandName} {product.modelName}
              </h6>
              <p className="text-muted small mb-1">{product.productName}</p>
              <p className="text-secondary small">
                {product.ramCapacity} • {product.storage} • {product.batteryCapacity}
              </p>
            </div>

            {/* Fiyat ve Platform */}
            <div className="card-footer bg-white border-0 text-center">
              <h5 className="fw-bold text-success mb-1">
                {product.discountedPrice
                  ? `${product.discountedPrice} TL`
                  : `${product.normalPrice} TL`}
              </h5>
              <span className="badge bg-secondary text-light" style={{ fontSize: "0.85rem" }}>
                {product.ecommercePageName}
              </span>
            </div>
          </div>
        </div>
      ))
    ) : (
      <div className="col-12 text-center">
        <p className="text-muted mt-4">Aradığınız kriterlere uygun ürün bulunamadı.</p>
      </div>
    )}
  </div>
</div>

      </div>
    </div>
  );
};

export default ProductList;
