import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler yüklenirken hata oluştu.", error);
      }
    }
    fetchProducts();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5" style={{ fontWeight: "bold", fontSize: "36px" }}>Ürün Listesi</h1>

      <div className="row">
        {products.length > 0 ? (
          products.map((product) => (
            <div
              key={product.productDetailID}
              className="col-md-4 mb-4 d-flex align-items-stretch"
              onClick={() => handleProductClick(product.productDetailID)}
              style={{ cursor: "pointer" }}
            >
              <div 
                className="card h-100 border-0 shadow-sm"
                style={{
                  borderRadius: "25px",
                  background: "linear-gradient(145deg, #f0f0f0, #ffffff)",
                  transition: "transform 0.3s ease, box-shadow 0.3s ease",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "scale(1.04)";
                  e.currentTarget.style.boxShadow = "0 10px 30px rgba(0, 0, 0, 0.2)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "scale(1)";
                  e.currentTarget.style.boxShadow = "0 4px 15px rgba(0, 0, 0, 0.1)";
                }}
              >
                <div className="card-body text-center">
                  <h4 className="card-title mb-3" style={{ fontWeight: "bold", fontSize: "24px" }}>
                    {product.brandName} {product.modelName}
                  </h4>

                  {/* Küçük ayırıcı çizgi */}
                  <div style={{
                    width: "50px",
                    height: "3px",
                    backgroundColor: "#198754",
                    margin: "0 auto 20px auto",
                    borderRadius: "5px"
                  }}></div>

                  <p className="card-text" style={{ fontSize: "16px", color: "#6c757d", lineHeight: "1.8" }}>
                    <strong>RAM:</strong> {product.ram}<br />
                    <strong>Depolama:</strong> {product.storage}<br />
                    <strong>Batarya:</strong> {product.battery}<br />
                    <strong>Renk:</strong> {product.color}
                  </p>
                </div>

                {/* Fiyat kısmı */}
                <div 
                  className="card-footer text-center bg-white border-0"
                  style={{
                    borderBottomLeftRadius: "25px",
                    borderBottomRightRadius: "25px",
                    background: "#d1e7dd",
                    padding: "20px",
                  }}
                >
                  <h5 className="text-success" style={{ fontWeight: "bold", fontSize: "22px", margin: "0" }}>
                    {product.price} TL
                  </h5>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">Ürün bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default ProductList;
