import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPlatformScores } from "../api/productApi";
import {
  BarChart, Bar, XAxis, YAxis, Tooltip,
  ResponsiveContainer, RadialBarChart, RadialBar
} from "recharts";
import "./ProductDetail.css";

const getLogo = (platform) => {
  switch (platform.toLowerCase()) {
    case "trendyol": return "/logos/trendyol.png";
    case "hepsiburada": return "/logos/hepsiburada.png";
    case "n11": return "/logos/n11.png";
    default: return "/logos/default.png";
  }
};

const ProductDetail = () => {
  const { productId } = useParams();
  const [scores, setScores] = useState([]);
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        const data = await getPlatformScores(productId);
        setScores(data);

        if (data.length > 0) {
          const p = data[0];
          setProduct({
            name: p.productName,
            image: p.productImageURL,
            ram: p.ramCapacity,
            storage: p.storage,
            battery: p.batteryCapacity,
            color: p.color,
            cpu: p.cpuFrequency,
            screen: p.screenSize
          });
        }
      } catch (err) {
        console.error("Veri alınamadı:", err);
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [productId]);

  return (
    <div className="product-detail-container">
      {/* Üst Tanıtım Alanı */}
      <div className="product-info">
        <h2 className="product-title">{product.name}</h2>
        <div className="product-info-grid">
          <div className="product-photo">
            <img src={product.image} alt={product.name} />
          </div>
          <div className="product-specs">
            <ul>
              <li><strong>RAM:</strong> {product.ram}</li>
              <li><strong>Depolama:</strong> {product.storage}</li>
              <li><strong>Batarya:</strong> {product.battery}</li>
              <li><strong>Renk:</strong> {product.color}</li>
              <li><strong>Ekran:</strong> {product.screen}</li>
              <li><strong>CPU:</strong> {product.cpu}</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Alt Skor Alanı */}
      <div className="platform-scores">
        {loading ? (
          <p className="text-center">Yükleniyor...</p>
        ) : scores.length === 0 ? (
          <p className="text-center text-danger">Skor verisi bulunamadı.</p>
        ) : (
          <div className="row">
            {scores.map((score, i) => (
              <div key={i} className="col-md-4 mb-4">
                <div className="card h-100 text-center p-3 shadow-sm">
                  <img
                    src={getLogo(score.platform)}
                    alt={score.platform}
                    className="score-logo mb-2"
                  />
                  <h5>{score.platform}</h5>

                  {/* Memnuniyet */}
                  <div style={{ width: "100%", height: 200 }}>
                    <ResponsiveContainer>
                      <RadialBarChart
                        cx="50%" cy="50%" innerRadius="70%" outerRadius="90%" barSize={10}
                        data={[{ name: "Memnuniyet", value: score.memnuniyet || 0, fill: "#00C49F" }]}
                        startAngle={90} endAngle={-270}
                      >
                        <RadialBar dataKey="value" />
                        <text x="50%" y="50%" textAnchor="middle" dominantBaseline="middle">
                          {score.memnuniyet ? `${score.memnuniyet.toFixed(1)}%` : "Yok"}
                        </text>
                      </RadialBarChart>
                    </ResponsiveContainer>
                  </div>

                  {/* Bar Skorlar */}
                  <div style={{ width: "100%", height: 150 }}>
                    <ResponsiveContainer>
                      <BarChart
                        layout="vertical"
                        data={[
                          { name: "Kalite", puan: score.kalite },
                          { name: "Fiyat/Performans", puan: score.fiyatPerformans },
                          { name: "Kargolama", puan: score.kargolama }
                        ]}
                      >
                        <XAxis type="number" domain={[0, 100]} hide />
                        <YAxis type="category" dataKey="name" />
                        <Tooltip />
                        <Bar dataKey="puan" fill="#007bff" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
