import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const defaultFilters = {
    brands: [],
    ram: [],
    storage: [],
    color: [],
    price: { min: "", max: "" },
  };
  const [pendingFilters, setPendingFilters] = useState(defaultFilters);
  const [activeFilters, setActiveFilters] = useState(defaultFilters);

  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler çekilirken hata oluştu:", error);
      }
    }
    fetchProducts();
  }, []);

  const uniqueBrands = [...new Set(products.map((p) => p.brandName))];
  const uniqueRAMs = [...new Set(products.map((p) => p.ramCapacity))];
  const uniqueStorages = [...new Set(products.map((p) => p.storage))];
  const uniqueColors = [...new Set(products.map((p) => p.color))];

  const handleCheckboxChange = (field, value) => {
    setPendingFilters((prev) => {
      const arr = prev[field];
      return {
        ...prev,
        [field]: arr.includes(value)
          ? arr.filter((item) => item !== value)
          : [...arr, value],
      };
    });
  };

  const filtered = products.filter((product) => {
    const matchesSearch =
      (product.brandName + " " + product.modelName + " " + product.productName)
        .toLowerCase()
        .includes((searchTerm || "").toLowerCase());

    const matchesBrand =
      activeFilters.brands.length === 0 ||
      activeFilters.brands.includes(product.brandName);

    const matchesRAM =
      activeFilters.ram.length === 0 ||
      activeFilters.ram.includes(product.ramCapacity);

    const matchesStorage =
      activeFilters.storage.length === 0 ||
      activeFilters.storage.includes(product.storage);

    const matchesColor =
      activeFilters.color.length === 0 ||
      activeFilters.color.includes(product.color);

    const price = product.discountedPrice || product.normalPrice || 0;
    const minOk =
      activeFilters.price.min === "" ||
      price >= parseFloat(activeFilters.price.min);
    const maxOk =
      activeFilters.price.max === "" ||
      price <= parseFloat(activeFilters.price.max);

    return (
      matchesSearch &&
      matchesBrand &&
      matchesRAM &&
      matchesStorage &&
      matchesColor &&
      minOk &&
      maxOk
    );
  });

  return (
    <div className="container-fluid mt-4">
      <div className="row">
        {/* FİLTRELEME PANELİ */}
        <div className="col-lg-3 mb-4">
          <div className="bg-light p-4 shadow-sm rounded sticky-top" style={{ top: "80px" }}>
            <h5 className="mb-3 text-primary border-bottom pb-2">🔍 Filtrele</h5>

            {/* Ortak checkbox alanı */}
            {[
              { title: "Marka", key: "brands", values: uniqueBrands },
              { title: "RAM", key: "ram", values: uniqueRAMs },
              { title: "Depolama", key: "storage", values: uniqueStorages },
              { title: "Renk", key: "color", values: uniqueColors },
            ].map(({ title, key, values }) => (
              <div key={key} className="mb-3">
                <strong>{title}</strong>
                {values.map((val) => (
                  <div className="form-check" key={val}>
                    <input
                      className="form-check-input"
                      type="checkbox"
                      id={`${key}-${val}`}
                      checked={pendingFilters[key].includes(val)}
                      onChange={() => handleCheckboxChange(key, val)}
                    />
                    <label className="form-check-label" htmlFor={`${key}-${val}`}>
                      {val}
                    </label>
                  </div>
                ))}
              </div>
            ))}

            {/* Fiyat */}
            <div className="mb-4">
              <strong>Fiyat</strong>
              <div className="d-flex gap-2">
                <input
                  type="number"
                  className="form-control form-control-sm"
                  placeholder="Min"
                  value={pendingFilters.price.min}
                  onChange={(e) =>
                    setPendingFilters({
                      ...pendingFilters,
                      price: { ...pendingFilters.price, min: e.target.value },
                    })
                  }
                />
                <input
                  type="number"
                  className="form-control form-control-sm"
                  placeholder="Max"
                  value={pendingFilters.price.max}
                  onChange={(e) =>
                    setPendingFilters({
                      ...pendingFilters,
                      price: { ...pendingFilters.price, max: e.target.value },
                    })
                  }
                />
              </div>
            </div>

            <div className="d-grid gap-2">
              <button
                className="btn btn-success"
                onClick={() => setActiveFilters({ ...pendingFilters })}
              >
                Uygula
              </button>
              <button
                className="btn btn-outline-secondary"
                onClick={() => {
                  setActiveFilters(defaultFilters);
                  setPendingFilters(defaultFilters);
                }}
              >
                Temizle
              </button>
            </div>
          </div>
        </div>

        {/* ÜRÜNLER */}
        <div className="col-lg-9">
          <h2 className="text-center text-dark fw-bold mb-4">
            <i className="bi bi-box-seam text-danger me-2"></i>Ürünler
          </h2>
          <div className="row">
            {filtered.length > 0 ? (
              filtered.map((product) => (
                <div
                  key={product.productID}
                  className="col-12 col-sm-6 col-md-4 col-xl-3 mb-4"
                  onClick={() => navigate(`/product/${product.productID}`)}
                  style={{ cursor: "pointer" }}
                >
                  <div
                    className="card shadow-sm h-100 border-0"
                    style={{
                      transition: "transform 0.2s",
                      background: "#fff5f5",
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.transform = "scale(1.03)";
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.transform = "scale(1)";
                    }}
                  >
                    <div className="card-body text-center">
                      <img
                        src={product.ecommercePageLogo || "/logos/default.png"}
                        alt="Logo"
                        style={{ height: "40px", marginBottom: "10px" }}
                      />
                      <img
                        src="/img/placeholder-phone.png"
                        className="img-fluid mb-2"
                        alt="Ürün görseli"
                        style={{ height: "100px", objectFit: "contain" }}
                      />
                      <h6 className="fw-bold">
                        {product.brandName} {product.modelName}
                      </h6>
                      <p className="text-muted small">{product.productName}</p>
                      <p className="text-secondary small mb-2">
                        {product.ramCapacity} • {product.storage} • {product.batteryCapacity}
                      </p>
                      <h6 className="text-success fw-bold">
                        {product.discountedPrice || product.normalPrice} TL
                      </h6>
                    </div>
                    <div className="card-footer text-center bg-white border-0">
                      <span className="badge bg-dark">{product.ecommercePageName}</span>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="col-12 text-center">
                <p className="text-muted">Aradığınız kritere uygun ürün bulunamadı.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductList;
