import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPlatformScores, getProducts } from "../api/productApi"; // ðŸ”¥ getProducts da import edildi
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, RadialBarChart, RadialBar } from "recharts";

// Platform logosunu getiren fonksiyon
const getPlatformLogo = (platformName) => {
  switch (platformName.toLowerCase()) {
    case "trendyol":
      return "/logos/trendyol.png";
    case "hepsiburada":
      return "/logos/hepsiburada.png";
    case "n11":
      return "/logos/n11.png";
    default:
      return "/logos/default.png"; 
  }
};

const ProductDetail = () => {
  const { id } = useParams();
  const [platformData, setPlatformData] = useState([]);
  const [productInfo, setProductInfo] = useState(null); // ðŸ”¥ ÃœrÃ¼n bilgisi state

  useEffect(() => {
    async function fetchData() {
      try {
        const platformScores = await getPlatformScores(id);
        setPlatformData(platformScores);

        const allProducts = await getProducts(); // ðŸ”¥ TÃ¼m Ã¼rÃ¼nleri Ã§ekiyoruz
        const selectedProduct = allProducts.find(p => p.productDetailID === parseInt(id));
        setProductInfo(selectedProduct); // ðŸ”¥ Ä°lgili Ã¼rÃ¼nÃ¼ bulduk
      } catch (error) {
        console.error("Veriler yÃ¼klenirken hata oluÅŸtu.", error);
      }
    }
    fetchData();
  }, [id]);

  return (
    <div className="container mt-4">
      {/* ÃœrÃ¼n AdÄ± */}
      <h1 className="text-center mb-5">
        {productInfo 
          ? `${productInfo.brandName} ${productInfo.modelName} ÃœrÃ¼n DetayÄ±` 
          : "ÃœrÃ¼n YÃ¼kleniyor..."}
      </h1>

      {/* Platform KartlarÄ± */}
      <div className="row">
        {platformData.length > 0 ? (
          platformData.map((platform, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div 
                className="card h-100 shadow-sm border-0"
                style={{
                  borderRadius: "20px",
                  background: "linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)",
                  transition: "transform 0.3s ease, boxShadow 0.3s ease",
                  cursor: "pointer"
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "scale(1.03)";
                  e.currentTarget.style.boxShadow = "0 8px 20px rgba(0,0,0,0.2)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "scale(1)";
                  e.currentTarget.style.boxShadow = "0 4px 12px rgba(0,0,0,0.1)";
                }}
              >
                <div className="card-body text-center">
                  
                  {/* Logo + Platform AdÄ± */}
                  <div className="text-center mb-4">
                    <img 
                      src={getPlatformLogo(platform.platform)}
                      alt={platform.platform}
                      style={{ width: "80px", height: "auto", marginBottom: "10px" }}
                    />
                    <h4 className="card-title">{platform.platform}</h4>
                  </div>

                  {/* Memnuniyet Radial GrafiÄŸi */}
                  <div style={{ width: "100%", height: 200, marginBottom: "20px" }}>
                    <ResponsiveContainer>
                      <RadialBarChart
                        cx="50%"
                        cy="50%"
                        innerRadius="60%"
                        outerRadius="90%"
                        barSize={15}
                        data={[{ name: "Memnuniyet", value: platform.memnuniyet, fill: "#00C49F" }]}
                        startAngle={90}
                        endAngle={-270}
                      >
                        <RadialBar minAngle={15} background clockWise dataKey="value" />
                        <text
                          x="50%"
                          y="50%"
                          textAnchor="middle"
                          dominantBaseline="middle"
                          style={{ fontSize: 22 }}
                        >
                          {platform.memnuniyet}%
                        </text>
                      </RadialBarChart>
                    </ResponsiveContainer>
                  </div>

                  {/* Kalite, Fiyat/Performans, Kargolama Bar Grafikleri */}
                  <div style={{ width: "100%", height: 150 }}>
                    <ResponsiveContainer>
                      <BarChart
                        layout="vertical"
                        data={[
                          { name: "Kalite", puan: platform.kalite },
                          { name: "Fiyat/Performans", puan: platform.fiyatPerformans },
                          { name: "Kargolama", puan: platform.kargolama },
                        ]}
                        margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                      >
                        <XAxis type="number" domain={[0, 100]} hide />
                        <YAxis type="category" dataKey="name" />
                        <Tooltip />
                        <Bar dataKey="puan" fill="#007bff" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>

                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">Platform verisi bulunamadÄ±.</p>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
