import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPlatformScores } from "../api/productApi";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, RadialBarChart, RadialBar } from "recharts";

const ProductDetail = () => {
  const { id } = useParams();
  const [platformData, setPlatformData] = useState([]);

  useEffect(() => {
    async function fetchPlatformScores() {
      try {
        const data = await getPlatformScores(id);
        setPlatformData(data);
      } catch (error) {
        console.error("Platform skorları yüklenirken hata oluştu.");
      }
    }
    fetchPlatformScores();
  }, [id]);

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-5">Ürün Detayı - ID: {id}</h1>

      {platformData.length > 0 ? (
        <div className="card shadow p-4">
          <h3 className="text-center mb-4">Platform Karşılaştırması</h3>

          <div className="row">
            {platformData.map((platform, index) => (
              <div key={index} className="col-md-4 text-center mb-4">
                <h5>{platform.platform}</h5>

                {/* Memnuniyet için küçük yuvarlak grafik */}
                <div style={{ width: "100%", height: 200 }}>
                  <ResponsiveContainer>
                    <RadialBarChart
                      cx="50%"
                      cy="50%"
                      innerRadius="60%"
                      outerRadius="90%"
                      barSize={15}
                      data={[
                        { name: "Memnuniyet", value: platform.memnuniyet, fill: "#00C49F" }
                      ]}
                      startAngle={90}
                      endAngle={-270}
                    >
                      <RadialBar minAngle={15} background clockWise dataKey="value" />
                      <text
                        x="50%"
                        y="50%"
                        textAnchor="middle"
                        dominantBaseline="middle"
                        className="progress-label"
                        style={{ fontSize: 20 }}
                      >
                        {platform.memnuniyet}%
                      </text>
                    </RadialBarChart>
                  </ResponsiveContainer>
                </div>

                {/* Alt tarafta küçük bar grafik */}
                <div style={{ height: 200 }}>
                  <ResponsiveContainer>
                    <BarChart
                      layout="vertical"
                      data={[
                        { name: "Kalite", puan: platform.kalite },
                        { name: "Fiyat/Performans", puan: platform.fiyatPerformans },
                        { name: "Kargolama", puan: platform.kargolama },
                      ]}
                      margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                    >
                      <XAxis type="number" domain={[0, 100]} hide />
                      <YAxis type="category" dataKey="name" />
                      <Tooltip />
                      <Bar dataKey="puan" fill="#007bff" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>

              </div>
            ))}
          </div>
        </div>
      ) : (
        <p className="text-center">Platform verisi bulunamadı.</p>
      )}
    </div>
  );
};

export default ProductDetail;
