import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";
import FilterSidebar from "../components/FilterSidebar";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const [filters, setFilters] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler yüklenirken hata oluştu.");
      }
    }
    fetchProducts();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const filteredProducts = products.filter((product) => {
    const matchesSearch =
      `${product.brandName} ${product.modelName}`.toLowerCase().includes(
        searchTerm.toLowerCase()
      );

    const matchesRAM = Object.keys(filters)
      .filter((key) => filters[key] && key.includes("GB"))
      .some((ram) => product.ram?.includes(ram)) || Object.keys(filters).every((key) => !filters[key]);

    const matchesPlatform = Object.keys(filters)
      .filter((key) => filters[key] && ["trendyol", "hepsiburada", "n11"].includes(key))
      .some((platform) => product.platform?.toLowerCase() === platform) || Object.keys(filters).every((key) => !filters[key]);

    return matchesSearch && matchesRAM && matchesPlatform;
  });

  return (
    <div style={{ display: "flex" }}>
      <FilterSidebar filters={filters} setFilters={setFilters} />

      <div className="container-fluid" style={{ paddingLeft: "30px" }}>
        <h2 className="mt-4 mb-4">Ürünler</h2>
        <div className="row">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div
                key={product.productID}
                className="col-md-4 mb-4"
                onClick={() => handleProductClick(product.productID)}
                style={{ cursor: "pointer" }}
              >
                <div className="card h-100 shadow-sm border-0">
                  <div className="card-body text-center">
                    <h5 className="card-title">
                      {product.brandName} {product.modelName}
                    </h5>
                    <p className="card-text">
                      RAM: {product.ramCapacity} <br />
                      Depolama: {product.storage} <br />
                      Batarya: {product.batteryCapacity} <br />
                      Renk: {product.color}
                    </p>
                  </div>
                  <div className="card-footer bg-white border-0 text-center">
                    <h6 className="text-success fw-bold">
                      {product.discountedPrice || product.normalPrice} TL
                    </h6>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-muted">Filtrelere uyan ürün bulunamadı.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductList;
