import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const [selectedBrands, setSelectedBrands] = useState([]);
  const [selectedRAM, setSelectedRAM] = useState([]);
  const [selectedStorage, setSelectedStorage] = useState([]);
  const [selectedColor, setSelectedColor] = useState([]);
  const [priceRange, setPriceRange] = useState({ min: "", max: "" });
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler çekilirken hata oluştu:", error);
      }
    }
    fetchProducts();
  }, []);

  const handleCheckboxChange = (setter, value) => {
    setter((prev) =>
      prev.includes(value) ? prev.filter((item) => item !== value) : [...prev, value]
    );
  };

  const uniqueBrands = [...new Set(products.map((p) => p.brandName))];
  const uniqueRAMs = [...new Set(products.map((p) => p.ramCapacity))];
  const uniqueStorages = [...new Set(products.map((p) => p.storage))];
  const uniqueColors = [...new Set(products.map((p) => p.color))];

  const filtered = products.filter((product) => {
    const matchesSearch =
      (product.brandName + " " + product.modelName + " " + product.productName)
        .toLowerCase()
        .includes((searchTerm || "").toLowerCase());

    const matchesBrand = selectedBrands.length === 0 || selectedBrands.includes(product.brandName);
    const matchesRAM = selectedRAM.length === 0 || selectedRAM.includes(product.ramCapacity);
    const matchesStorage = selectedStorage.length === 0 || selectedStorage.includes(product.storage);
    const matchesColor = selectedColor.length === 0 || selectedColor.includes(product.color);

    const price = product.discountedPrice || product.normalPrice || 0;
    const minOk = priceRange.min === "" || price >= parseFloat(priceRange.min);
    const maxOk = priceRange.max === "" || price <= parseFloat(priceRange.max);

    return matchesSearch && matchesBrand && matchesRAM && matchesStorage && matchesColor && minOk && maxOk;
  });

  return (
    <div className="container-fluid mt-4">
      <div className="row">
        {/* Sol Filtreleme Paneli */}
        <div className="col-md-3 mb-4">
          <div className="p-3 border rounded shadow-sm sticky-top" style={{ top: "80px" }}>
            <h5 className="mb-3">Filtrele</h5>

            <strong className="d-block mb-2">Marka</strong>
            {uniqueBrands.map((brand) => (
              <div key={brand} className="form-check mb-2">
                <input
                  className="form-check-input"
                  type="checkbox"
                  id={`brand-${brand}`}
                  checked={selectedBrands.includes(brand)}
                  onChange={() => handleCheckboxChange(setSelectedBrands, brand)}
                />
                <label className="form-check-label" htmlFor={`brand-${brand}`}>
                  {brand}
                </label>
              </div>
            ))}

            <strong className="d-block mt-3">RAM</strong>
            {uniqueRAMs.map((ram) => (
              <div key={ram} className="form-check mb-1">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id={`ram-${ram}`}
                  checked={selectedRAM.includes(ram)}
                  onChange={() => handleCheckboxChange(setSelectedRAM, ram)}
                />
                <label className="form-check-label" htmlFor={`ram-${ram}`}>
                  {ram}
                </label>
              </div>
            ))}

            <strong className="d-block mt-3">Depolama</strong>
            {uniqueStorages.map((storage) => (
              <div key={storage} className="form-check mb-1">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id={`storage-${storage}`}
                  checked={selectedStorage.includes(storage)}
                  onChange={() => handleCheckboxChange(setSelectedStorage, storage)}
                />
                <label className="form-check-label" htmlFor={`storage-${storage}`}>
                  {storage}
                </label>
              </div>
            ))}

            <strong className="d-block mt-3">Renk</strong>
            {uniqueColors.map((color) => (
              <div key={color} className="form-check mb-1">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id={`color-${color}`}
                  checked={selectedColor.includes(color)}
                  onChange={() => handleCheckboxChange(setSelectedColor, color)}
                />
                <label className="form-check-label" htmlFor={`color-${color}`}>
                  {color}
                </label>
              </div>
            ))}

            <strong className="d-block mt-3">Fiyat Aralığı</strong>
            <div className="d-flex gap-2">
              <input
                type="number"
                placeholder="Min"
                className="form-control form-control-sm"
                value={priceRange.min}
                onChange={(e) => setPriceRange({ ...priceRange, min: e.target.value })}
              />
              <input
                type="number"
                placeholder="Max"
                className="form-control form-control-sm"
                value={priceRange.max}
                onChange={(e) => setPriceRange({ ...priceRange, max: e.target.value })}
              />
            </div>
          </div>
        </div>

        {/* Ürün Kartları */}
        <div className="col-md-9">
          <h2 className="text-center mb-4">Ürünler</h2>
          <div className="row">
            {filtered.map((product) => (
              <div
                key={product.productID}
                className="col-12 col-sm-6 col-lg-4 col-xl-3 mb-4"
                onClick={() => navigate(`/product/${product.productID}`)}
                style={{ cursor: "pointer" }}
              >
                <div
                  className="card h-100 border-0 shadow-sm"
                  style={{ transition: "transform 0.2s ease-in-out" }}
                  onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.02)")}
                  onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
                >
                  <div className="card-body text-center">
                    <img
                      src={product.ecommercePageLogo}
                      alt={product.ecommercePageName}
                      style={{ width: 60 }}
                      className="mb-2"
                    />
                    <h5 className="card-title">
                      {product.brandName} {product.modelName}
                    </h5>
                    <p className="text-muted small mb-1">{product.productName}</p>
                    <p className="text-secondary small">
                      {product.ramCapacity} / {product.storage} / {product.batteryCapacity}
                    </p>
                  </div>
                  <div className="card-footer bg-white text-center border-0">
                    <h6 className="fw-bold text-success">
                      {product.discountedPrice
                        ? `${product.discountedPrice} TL`
                        : `${product.normalPrice} TL`}
                    </h6>
                    <span className="badge bg-light text-dark mt-2" style={{ fontSize: "0.85rem" }}>
                      {product.ecommercePageName}
                    </span>
                  </div>
                </div>
              </div>
            ))}
            {filtered.length === 0 && (
              <div className="col-12 text-center">
                <p className="text-muted mt-4">Aradığınız kriterlere uygun ürün bulunamadı.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductList;
