import Header from "./components/Header";
import Footer from "./components/Footer";

const App = () => {
  const [searchTerm, setSearchTerm] = useState(""); // ðŸ”¥ Arama Terimi Burada!

  return (
    <Router>
      <Header setSearchTerm={setSearchTerm} /> {/* Header'a gÃ¶nderiyoruz */}

      <Routes>
        <Route path="/" element={<ProductList searchTerm={searchTerm} />} />
        <Route path="/product/:id" element={<ProductDetail />} />
      </Routes>

      <Footer />
    </Router>
  );
};

export default App;
