import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";
import "./ProductList.css";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const defaultFilters = {
    brands: [],
    ram: [],
    storage: [],
    color: [],
    price: { min: "", max: "" },
  };
  const [pendingFilters, setPendingFilters] = useState(defaultFilters);
  const [activeFilters, setActiveFilters] = useState(defaultFilters);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        console.log("Gelen ürünler:", data); // API'den gelen veri
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          console.error("API'den beklenen formatta veri gelmedi.");
          setProducts([]);
        }
      } catch (error) {
        console.error("Ürünler çekilirken hata oluştu:", error);
      }
    }
    fetchProducts();
  }, []);

  // Unique filtre seçenekleri (null veya undefined olanları temizle)
  const uniqueBrands = [
    ...new Set(products.map((p) => p.brandName).filter(Boolean)),
  ];
  const uniqueRAMs = [
    ...new Set(products.map((p) => p.ramCapacity).filter(Boolean)),
  ];
  const uniqueStorages = [
    ...new Set(products.map((p) => p.storage).filter(Boolean)),
  ];
  const uniqueColors = [...new Set(products.map((p) => p.color).filter(Boolean))];

  const handleCheckboxChange = (field, value) => {
    setPendingFilters((prev) => {
      const arr = prev[field];
      return {
        ...prev,
        [field]: arr.includes(value)
          ? arr.filter((item) => item !== value)
          : [...arr, value],
      };
    });
  };

  // Filtrelenmiş ürünler
  const filtered = products.filter((product) => {
    const brandName = product.brandName || "";
    const modelName = product.modelName || "";
    const productName = product.productName || "";

    const matchesSearch = (
      brandName + " " + modelName + " " + productName
    )
      .toLowerCase()
      .includes((searchTerm || "").toLowerCase());

    const matchesBrand =
      activeFilters.brands.length === 0 || activeFilters.brands.includes(brandName);

    const matchesRAM =
      activeFilters.ram.length === 0 || activeFilters.ram.includes(product.ramCapacity);

    const matchesStorage =
      activeFilters.storage.length === 0 || activeFilters.storage.includes(product.storage);

    const matchesColor =
      activeFilters.color.length === 0 || activeFilters.color.includes(product.color);

    const price = product.discountedPrice || product.normalPrice || 0;
    const minOk = activeFilters.price.min === "" || price >= parseFloat(activeFilters.price.min);
    const maxOk = activeFilters.price.max === "" || price <= parseFloat(activeFilters.price.max);

    return matchesSearch && matchesBrand && matchesRAM && matchesStorage && matchesColor && minOk && maxOk;
  });

  // Model bazında tekil listeleme modelName bazlı
  const uniqueModelsMap = new Map();
  filtered.forEach((p) => {
    const key = p.modelName;
    if (key && !uniqueModelsMap.has(key)) {
      uniqueModelsMap.set(key, p);
    }
  });
  const uniqueModels = Array.from(uniqueModelsMap.values());

  return (
    <div className="product-list-container container-fluid mt-4">
      <div className="row">
        {/* Sol Filtre Paneli */}
        <div className="col-md-3 mb-4">
          <div className="product-filter-panel shadow-sm rounded-4 p-3 bg-white">
            <h5 className="filter-title border-bottom pb-2">🎯 Filtrele</h5>

            <FilterSection
              label="Marka"
              options={uniqueBrands}
              field="brands"
              selected={pendingFilters.brands}
              onChange={handleCheckboxChange}
            />
            <FilterSection
              label="RAM"
              options={uniqueRAMs}
              field="ram"
              selected={pendingFilters.ram}
              onChange={handleCheckboxChange}
            />
            <FilterSection
              label="Depolama"
              options={uniqueStorages}
              field="storage"
              selected={pendingFilters.storage}
              onChange={handleCheckboxChange}
            />
            <FilterSection
              label="Renk"
              options={uniqueColors}
              field="color"
              selected={pendingFilters.color}
              onChange={handleCheckboxChange}
            />

            <strong className="d-block mt-3">Fiyat</strong>
            <div className="d-flex gap-2 mb-3">
              <input
                type="number"
                className="form-control form-control-sm"
                placeholder="Min"
                value={pendingFilters.price.min}
                onChange={(e) =>
                  setPendingFilters({
                    ...pendingFilters,
                    price: { ...pendingFilters.price, min: e.target.value },
                  })
                }
              />
              <input
                type="number"
                className="form-control form-control-sm"
                placeholder="Max"
                value={pendingFilters.price.max}
                onChange={(e) =>
                  setPendingFilters({
                    ...pendingFilters,
                    price: { ...pendingFilters.price, max: e.target.value },
                  })
                }
              />
            </div>

            <div className="d-grid gap-2 mt-3">
              <button
                className="btn btn-primary fw-bold py-2 rounded-3 shadow-sm"
                onClick={() => setActiveFilters({ ...pendingFilters })}
              >
                🔍 Filtrele
              </button>

              <button
                className="btn btn-outline-dark py-2 rounded-3"
                onClick={() => {
                  setPendingFilters(defaultFilters);
                  setActiveFilters(defaultFilters);
                }}
              >
                ♻️ Temizle
              </button>
            </div>
          </div>
        </div>

        {/* Model Kartları */}
        <div className="col-md-9">
          <h2 className="product-title">📱 Modeller</h2>
          <div className="row">
            {uniqueModels.length > 0 ? (
              uniqueModels.map((model) => (
                <div
                  key={model.modelName}
                  className="col-12 col-sm-6 col-lg-4 col-xl-3 mb-4"
                  onClick={() => navigate(`/model/${encodeURIComponent(model.modelName)}`)}
                  style={{ cursor: "pointer" }}
                >
                  <div className="product-card shadow-sm rounded-4 p-2 h-100">
                    <div className="product-image-wrapper text-center">
                      <img
                        src={model.productImageURL || "/logos/default.png"}
                        alt={model.modelName}
                        className="product-image img-fluid"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = "/img/default-image.png";
                        }}
                      />
                    </div>
                    <div className="product-card-body text-center mt-2">
                      <h6 className="fw-bold">{model.modelName}</h6>
                      <p className="text-muted small mb-2">{model.brandName}</p>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="col-12 text-center">
                <p className="text-muted mt-4">Aradığınız kriterlere uygun model bulunamadı.</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const FilterSection = ({ label, options, field, selected, onChange }) => (
  <>
    <strong className="d-block mt-3">{label}</strong>
    {options.map((val) => (
      <div key={val} className="form-check mb-1">
        <input
          className="form-check-input"
          type="checkbox"
          id={`${field}-${val}`}
          checked={selected.includes(val)}
          onChange={() => onChange(field, val)}
        />
        <label className="form-check-label" htmlFor={`${field}-${val}`}>
          {val}
        </label>
      </div>
    ))}
  </>
);

export default ProductList;
