import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler yüklenirken hata oluştu.", error);
      }
    }
    fetchProducts();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-5">Ürün Listesi</h1>

      <div className="row">
        {products.length > 0 ? (
          products.map((product) => (
            <div
              key={product.productDetailID}
              className="col-md-4 mb-4"
              onClick={() => handleProductClick(product.productDetailID)}
              style={{ cursor: "pointer" }}
            >
              <div 
                className="card h-100 shadow-sm border-0"
                style={{
                  borderRadius: "20px",
                  background: "linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%)",
                  transition: "transform 0.3s ease, box-shadow 0.3s ease",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "scale(1.02)";
                  e.currentTarget.style.boxShadow = "0 8px 20px rgba(0,0,0,0.2)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "scale(1)";
                  e.currentTarget.style.boxShadow = "0 4px 12px rgba(0,0,0,0.1)";
                }}
              >
                <div className="card-body text-center">
                  <h4 className="card-title mb-3" style={{ fontWeight: "bold" }}>
                    {product.brandName} {product.modelName}
                  </h4>
                  <p className="card-text" style={{ fontSize: "16px", color: "#6c757d" }}>
                    RAM: {product.ram}<br />
                    Depolama: {product.storage}<br />
                    Batarya: {product.battery}<br />
                    Renk: {product.color}
                  </p>
                </div>
                <div className="card-footer text-center bg-white border-0">
                  <h5 className="text-success" style={{ fontWeight: "bold", fontSize: "20px" }}>
                    {product.price} TL
                  </h5>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">Ürün bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default ProductList;
