import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";
import "./ProductList.css";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("ÃœrÃ¼nler Ã§ekilirken hata oluÅŸtu:", error);
      }
    }
    fetchProducts();
  }, []);

  // Benzersiz Ã¼rÃ¼nleri sadece productName'e gÃ¶re grupla
  const uniqueProductMap = new Map();
  products.forEach((product) => {
    if (!uniqueProductMap.has(product.productName)) {
      uniqueProductMap.set(product.productName, product);
    }
  });

  const uniqueProducts = Array.from(uniqueProductMap.values());

  // Arama filtresi (isteÄŸe baÄŸlÄ±)
  const filtered = uniqueProducts.filter((product) =>
    product.productName.toLowerCase().includes((searchTerm || "").toLowerCase())
  );

  return (
    <div className="product-list-container container-fluid mt-4">
      <h2 className="product-title">ðŸ“± ÃœrÃ¼nler</h2>
      <div className="row">
        {filtered.length > 0 ? (
          filtered.map((product) => (
            <div
              key={product.productName}
              className="col-12 col-sm-6 col-lg-4 col-xl-3 mb-4"
              onClick={() => navigate(`/product/${encodeURIComponent(product.productName)}`)}
              style={{ cursor: "pointer" }}
            >
              <div className="product-card shadow-sm rounded-4 p-2 h-100">
                <div className="product-image-wrapper text-center">
                  <img
                    src={product.productImageURL || "/logos/default.png"}
                    alt={product.productName}
                    className="product-image img-fluid"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "/img/default-image.png";
                    }}
                  />
                </div>

                <div className="product-card-body text-center mt-2">
                  <h6 className="fw-bold">{product.productName}</h6>
                  <p className="text-muted small mb-2">{product.brandName}</p>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-12 text-center">
            <p className="text-muted mt-4">AradÄ±ÄŸÄ±nÄ±z kriterlere uygun Ã¼rÃ¼n bulunamadÄ±.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductList;
