import axios from "axios";

const BASE_URL = "https://localhost:44365/api";  // üî• Burada kendi backend API adresini kullanƒ±yoruz!

export const getProducts = async () => {
  try {
    const response = await axios.get(`${BASE_URL}/Product`);
    return response.data;
  } catch (error) {
    console.error("√úr√ºnler √ßekilirken hata olu≈ütu:", error);
    throw error;
  }
};
