import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler yüklenirken hata oluştu.", error);
      }
    }
    fetchProducts();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div style={{ background: "#f8f9fa", minHeight: "100vh", padding: "60px 0" }}>
      <div className="container">
        
        {/* Üst Başlık */}
        <div className="text-center mb-5">
          <h1 style={{
            fontSize: "42px",
            fontWeight: "bold",
            color: "#212529",
            letterSpacing: "1px",
            marginBottom: "10px"
          }}>
            Ürünler
          </h1>
          <div style={{
            width: "80px",
            height: "4px",
            backgroundColor: "#198754",
            margin: "0 auto",
            borderRadius: "10px"
          }}></div>
        </div>

        {/* Ürün Kartları */}
        <div className="row">
          {products.length > 0 ? (
            products.map((product) => (
              <div
                key={product.productDetailID}
                className="col-md-4 mb-5 d-flex align-items-stretch"
                onClick={() => handleProductClick(product.productDetailID)}
                style={{ cursor: "pointer" }}
              >
                <div 
                  className="card h-100 shadow-sm border-0"
                  style={{
                    borderRadius: "20px",
                    background: "linear-gradient(135deg, #ffffff, #f1f3f5)",
                    transition: "transform 0.3s ease, box-shadow 0.3s ease",
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.transform = "scale(1.03)";
                    e.currentTarget.style.boxShadow = "0 10px 25px rgba(0, 0, 0, 0.15)";
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.transform = "scale(1)";
                    e.currentTarget.style.boxShadow = "0 4px 12px rgba(0, 0, 0, 0.1)";
                  }}
                >
                  <div className="card-body text-center">
                    <h4 className="card-title mb-3" style={{ fontWeight: "bold", fontSize: "24px", color: "#343a40" }}>
                      {product.brandName} {product.modelName}
                    </h4>

                    <p className="card-text" style={{ fontSize: "16px", color: "#6c757d", lineHeight: "1.8" }}>
                      RAM: {product.ram}<br />
                      Depolama: {product.storage}<br />
                      Batarya: {product.battery}<br />
                      Renk: {product.color}
                    </p>
                  </div>

                  <div 
                    className="card-footer text-center bg-white border-0"
                    style={{
                      borderBottomLeftRadius: "20px",
                      borderBottomRightRadius: "20px",
                      padding: "20px",
                      background: "#e9f7ef",
                    }}
                  >
                    <h5 className="text-success" style={{ fontWeight: "bold", fontSize: "22px", margin: "0" }}>
                      {product.price} TL
                    </h5>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-center">Ürün bulunamadı.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductList;
