import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";
import "../styles/ProductList.css"; // ⬅️ Harici CSS dosyası

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const [pendingFilters, setPendingFilters] = useState({
    brands: [],
    ram: [],
    storage: [],
    color: [],
    price: { min: "", max: "" },
  });
  const [activeFilters, setActiveFilters] = useState({ ...pendingFilters });

  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      const data = await getProducts();
      setProducts(data);
    }
    fetchProducts();
  }, []);

  const uniqueBrands = [...new Set(products.map(p => p.brandName))];
  const uniqueRAMs = [...new Set(products.map(p => p.ramCapacity))];
  const uniqueStorages = [...new Set(products.map(p => p.storage))];
  const uniqueColors = [...new Set(products.map(p => p.color))];

  const handleCheckboxChange = (field, value) => {
    setPendingFilters(prev => ({
      ...prev,
      [field]: prev[field].includes(value)
        ? prev[field].filter(item => item !== value)
        : [...prev[field], value]
    }));
  };

  const filtered = products.filter(product => {
    const price = product.discountedPrice || product.normalPrice || 0;
    return (
      (product.brandName + product.modelName + product.productName)
        .toLowerCase().includes(searchTerm?.toLowerCase() || "") &&
      (activeFilters.brands.length === 0 || activeFilters.brands.includes(product.brandName)) &&
      (activeFilters.ram.length === 0 || activeFilters.ram.includes(product.ramCapacity)) &&
      (activeFilters.storage.length === 0 || activeFilters.storage.includes(product.storage)) &&
      (activeFilters.color.length === 0 || activeFilters.color.includes(product.color)) &&
      (!activeFilters.price.min || price >= parseFloat(activeFilters.price.min)) &&
      (!activeFilters.price.max || price <= parseFloat(activeFilters.price.max))
    );
  });

  return (
    <div className="product-list container-fluid mt-4">
      <div className="row">
        {/* Sol Filtre */}
        <div className="col-md-3 product-list__filters">
          <div className="product-list__filter-card shadow-sm">
            <h5 className="border-bottom pb-2 text-primary">Filtrele</h5>

            <FilterGroup title="Marka" options={uniqueBrands} field="brands" selected={pendingFilters.brands} onChange={handleCheckboxChange} />
            <FilterGroup title="RAM" options={uniqueRAMs} field="ram" selected={pendingFilters.ram} onChange={handleCheckboxChange} />
            <FilterGroup title="Depolama" options={uniqueStorages} field="storage" selected={pendingFilters.storage} onChange={handleCheckboxChange} />
            <FilterGroup title="Renk" options={uniqueColors} field="color" selected={pendingFilters.color} onChange={handleCheckboxChange} />

            <strong className="d-block mt-3">Fiyat</strong>
            <div className="d-flex gap-2 mb-3">
              <input type="number" className="form-control form-control-sm" placeholder="Min"
                value={pendingFilters.price.min}
                onChange={e => setPendingFilters(p => ({ ...p, price: { ...p.price, min: e.target.value } }))} />
              <input type="number" className="form-control form-control-sm" placeholder="Max"
                value={pendingFilters.price.max}
                onChange={e => setPendingFilters(p => ({ ...p, price: { ...p.price, max: e.target.value } }))} />
            </div>

            <div className="d-grid gap-2">
              <button className="btn btn-success" onClick={() => setActiveFilters({ ...pendingFilters })}>Filtrele</button>
              <button className="btn btn-outline-secondary" onClick={() => {
                setPendingFilters({ brands: [], ram: [], storage: [], color: [], price: { min: "", max: "" } });
                setActiveFilters({ brands: [], ram: [], storage: [], color: [], price: { min: "", max: "" } });
              }}>Temizle</button>
            </div>
          </div>
        </div>

        {/* Ürün Kartları */}
        <div className="col-md-9">
          <h2 className="product-list__title">
            <i className="bi bi-box-seam me-2"></i>Ürünler
          </h2>
          <div className="row">
            {filtered.length > 0 ? (
              filtered.map(product => (
                <div key={product.productID} className="col-sm-6 col-lg-4 col-xl-3 mb-4">
                  <div className="product-card" onClick={() => navigate(`/product/${product.productID}`)}>
                    <div className="product-card__image">
                      <img src={product.ecommercePageLogo || "/logos/default.png"} alt="Logo" />
                      <img src="/img/placeholder-phone.png" alt="Ürün" />
                    </div>
                    <div className="product-card__body">
                      <h6>{product.brandName} {product.modelName}</h6>
                      <p>{product.productName}</p>
                      <small>{product.ramCapacity} • {product.storage} • {product.batteryCapacity}</small>
                    </div>
                    <div className="product-card__footer">
                      <strong>{product.discountedPrice || product.normalPrice} TL</strong>
                      <span>{product.ecommercePageName}</span>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-center text-muted mt-4">Aradığınız kriterlere uygun ürün bulunamadı.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const FilterGroup = ({ title, options, field, selected, onChange }) => (
  <>
    <strong className="d-block mt-3">{title}</strong>
    {options.map(opt => (
      <div className="form-check" key={opt}>
        <input type="checkbox" className="form-check-input" id={`${field}-${opt}`}
          checked={selected.includes(opt)} onChange={() => onChange(field, opt)} />
        <label className="form-check-label" htmlFor={`${field}-${opt}`}>{opt}</label>
      </div>
    ))}
  </>
);

export default ProductList;
