import React, { useEffect, useState } from "react";
import { getProducts } from "../api/productApi";
import { useNavigate } from "react-router-dom";

const ProductList = ({ searchTerm }) => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await getProducts();
        setProducts(data);
      } catch (error) {
        console.error("Ürünler çekilirken hata oluştu:", error);
      }
    }
    fetchProducts();
  }, []);

  const filtered = products.filter(product =>
    (product.brandName + " " + product.modelName + " " + product.productName)
      .toLowerCase()
      .includes((searchTerm || "").toLowerCase())
  );

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Ürünler</h2>
      <div className="row">
        {filtered.map(product => (
          <div
            key={product.productID}
            className="col-12 col-md-6 col-lg-4 col-xl-3 mb-4"
            onClick={() => navigate(`/product/${product.productID}`)}
            style={{ cursor: "pointer" }}
          >
            <div className="card h-100 shadow-sm border-0">
              <div className="card-body text-center">
                <img src={product.ecommercePageLogo} alt={product.ecommercePageName} style={{ width: 60 }} />
                <h5 className="mt-3">{product.brandName} {product.modelName}</h5>
                <p style={{ fontSize: "14px", color: "#555" }}>{product.productName}</p>
                <p className="text-muted">{product.ramCapacity} / {product.storage} / {product.batteryCapacity}</p>
                <h6 className="text-success fw-bold">{product.discountedPrice} TL</h6>
              </div>
            </div>
          </div>
        ))}
        {filtered.length === 0 && <p className="text-center">Ürün bulunamadı.</p>}
      </div>
    </div>
  );
};

export default ProductList;
