import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getPlatformScores } from "../api/productApi";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, RadialBarChart, RadialBar } from "recharts";

const ProductDetail = () => {
  const { id } = useParams();
  const [platformData, setPlatformData] = useState([]);

  useEffect(() => {
    async function fetchPlatformScores() {
      try {
        const data = await getPlatformScores(id);
        setPlatformData(data);
      } catch (error) {
        console.error("Platform skorlarÄ± yÃ¼klenirken hata oluÅŸtu.");
      }
    }
    fetchPlatformScores();
  }, [id]);

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-5">ÃœrÃ¼n DetayÄ± - ID: {id}</h1>

      <div className="row">
        {platformData.length > 0 ? (
          platformData.map((platform, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card h-100 shadow-sm">
                <div className="card-body text-center">
                  <h5 className="card-title">{platform.platform}</h5>

                  {/* ðŸ”¥ Memnuniyet Ä°Ã§in BÃ¼yÃ¼k Yuvarlak Grafik */}
                  <div style={{ width: "100%", height: 200, marginBottom: "20px" }}>
                    <ResponsiveContainer>
                      <RadialBarChart
                        cx="50%"
                        cy="50%"
                        innerRadius="60%"
                        outerRadius="90%"
                        barSize={15}
                        data={[
                          { name: "Memnuniyet", value: platform.memnuniyet, fill: "#00C49F" }
                        ]}
                        startAngle={90}
                        endAngle={-270}
                      >
                        <RadialBar minAngle={15} background clockWise dataKey="value" />
                        <text
                          x="50%"
                          y="50%"
                          textAnchor="middle"
                          dominantBaseline="middle"
                          className="progress-label"
                          style={{ fontSize: 24 }}
                        >
                          {platform.memnuniyet}%
                        </text>
                      </RadialBarChart>
                    </ResponsiveContainer>
                  </div>

                  {/* ðŸ”¥ KÃ¼Ã§Ã¼k Grafikler */}
                  <ResponsiveContainer width="100%" height={150}>
                    <BarChart
                      layout="vertical"
                      data={[
                        { name: "Kalite", puan: platform.kalite },
                        { name: "Fiyat/Performans", puan: platform.fiyatPerformans },
                        { name: "Kargolama", puan: platform.kargolama },
                      ]}
                      margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                    >
                      <XAxis type="number" domain={[0, 100]} hide />
                      <YAxis type="category" dataKey="name" />
                      <Tooltip />
                      <Bar dataKey="puan" fill="#007bff" />
                    </BarChart>
                  </ResponsiveContainer>

                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">Platform verisi bulunamadÄ±.</p>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
