import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  BarChart, Bar, XAxis, YAxis, Tooltip,
  ResponsiveContainer, RadialBarChart, RadialBar
} from "recharts";
import "./ProductDetail.css";
import { getPlatformScores, getProductDetails } from "../api/productApi";


const getLogo = (platform) => {
  switch (platform.toLowerCase()) {
    case "trendyol": return "/logos/trendyol.png";
    case "hepsiburada": return "/logos/hepsiburada.png";
    case "n11": return "/logos/n11.png";
    default: return "/logos/default.png";
  }
};

const ProductDetail = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState({});
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        const [scoreData, productData] = await Promise.all([
          getPlatformScores(productId),
          getProductDetails(productId)
        ]);
  
        setScores(scoreData);
  
        if (productData) {
          setProduct({
            name: productData.productName,
            image: productData.productImageURL,
            ram: productData.ramCapacity,
            storage: productData.storage,
            battery: productData.batteryCapacity,
            color: productData.color,
            cpu: productData.cpuFrequency,
            screen: productData.screenSize
          });
        }
      } catch (err) {
        console.error("Veri alınamadı:", err);
      } finally {
        setLoading(false);
      }
    }
  
    fetchData();
  }, [productId]);
  
  

  return (
    <div className="product-detail-container">
      <h2 className="product-detail-title text-center">{product.name}</h2>

      <div className="product-overview d-flex justify-content-center align-items-start flex-wrap gap-4 mt-4">
        <div className="product-overview-specs">
          <ul>
            <li><strong>RAM:</strong> {product.ram}</li>
            <li><strong>Depolama:</strong> {product.storage}</li>
            <li><strong>Batarya:</strong> {product.battery}</li>
            <li><strong>Renk:</strong> {product.color}</li>
            <li><strong>Ekran:</strong> {product.screen}</li>
            <li><strong>CPU:</strong> {product.cpu}</li>
          </ul>
        </div>

        <div className="product-overview-image">
          <img src={product.image} alt={product.name} className="img-fluid" style={{ maxWidth: 300, borderRadius: 12 }} />
        </div>
      </div>

      {/* Skor kısmı */}
      <div className="platform-scores mt-5">
        {loading ? (
          <p className="text-center">Yükleniyor...</p>
        ) : scores.length === 0 ? (
          <p className="text-center text-danger">Skor verisi bulunamadı.</p>
        ) : (
          <div className="row">
            {scores.map((score, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card h-100 text-center p-3 shadow-sm">
                  <img
                    src={getLogo(score.platform)}
                    alt={score.platform}
                    style={{ height: 50 }}
                    className="mb-2 mx-auto"
                  />
                  <h5>{score.platform}</h5>

                  <div style={{ width: "100%", height: 200 }}>
                    <ResponsiveContainer>
                      <RadialBarChart
                        cx="50%" cy="50%" innerRadius="70%" outerRadius="90%" barSize={10}
                        data={[{ name: "Memnuniyet", value: score.memnuniyet || 0, fill: "#00C49F" }]}
                        startAngle={90} endAngle={-270}
                      >
                        <RadialBar dataKey="value" />
                        <text
                          x="50%" y="50%"
                          textAnchor="middle"
                          dominantBaseline="middle"
                          style={{ fontSize: "18px" }}
                        >
                          {score.memnuniyet ? `${score.memnuniyet.toFixed(1)}%` : "Yok"}
                        </text>
                      </RadialBarChart>
                    </ResponsiveContainer>
                  </div>

                  <div style={{ width: "100%", height: 150 }}>
                    <ResponsiveContainer>
                      <BarChart
                        layout="vertical"
                        data={[
                          { name: "Kalite", puan: score.kalite },
                          { name: "Fiyat/Performans", puan: score.fiyatPerformans },
                          { name: "Kargolama", puan: score.kargolama }
                        ]}
                      >
                        <XAxis type="number" domain={[0, 100]} hide />
                        <YAxis type="category" dataKey="name" />
                        <Tooltip />
                        <Bar dataKey="puan" fill="#007bff" />
                      </BarChart>
                    </ResponsiveContainer>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
